// Header Styles

.header {
  display:flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
  height: 5rem; // 80px
  max-width: $pageWidth;
  margin: 0 auto;


  .logo {
    width: 115px;
  }

