    font-size: 1rem;
    position: relative;

    &__burger-menu {
      width: 35px;
      height: 35px;
      position: relative;

      @media (min-width: $tablet) {
        display: none;
      }

      .burger-menu,
      .close-burger-menu {
        position: absolute;
        top: 0;
        left: 0;

      }

      .close-burger-menu {
